import React from 'react'
import Loader from "react-loader-spinner";

function CustomLoader(props) {
  alert(props.show)
  return (
    <div>
      {props.show && <div id="loading">
        <Loader
          className="loaingComponent"
          type="Puff"
          color="#00BFFF"
          height={100}
          width={100}
        />
      </div>
      }
    </div>
  );
}

export default CustomLoader;
