import React from 'react'

var id = 0 ;
 function StudioSections(props) {
  var nodes = props.data
  var addNodeHeader = (nodeNmae)=>{
    props.addNode({type:"addHeader",data:{name:nodeNmae}})
  }
  alert(JSON.stringify(nodes))
  return (
    <div>
       <div class="col-xs-3 studioColumn">
        <a href="#" onClick={(e)=>{e.preventDefault();addNodeHeader('header'+id);}}><h4 class="sectionHeader" id="addHeader">Add Header section &nbsp; <i class="fa fa-plus-circle" aria-hidden="true"></i></h4></a>
        {nodes.map((item)=>{
          if(item.type == "header")
          return    <h6 class="sectionItem"> <input type="text" placeholder={item.name} /> &nbsp; <i class="fa fa-minus-circle" aria-hidden="true"></i></h6>
          else return <h1>{item.type == "header"}</h1>
        })}
        <h4 class="sectionHeader">Add Menu items section &nbsp; <i class="fa fa-plus-circle" aria-hidden="true"></i></h4>
        <h6 class="sectionItem">item &nbsp; <i class="fa fa-minus-circle" aria-hidden="true"></i></h6>
        <h4 class="sectionHeader">Add Image section &nbsp; <i class="fa fa-plus-circle" aria-hidden="true"></i></h4>
        <h6 class="sectionItem">item &nbsp; <i class="fa fa-minus-circle" aria-hidden="true"></i></h6>
        <h6 class="sectionItem">item item item item &nbsp; <i class="fa fa-minus-circle" aria-hidden="true"></i></h6>
        <h4 class="sectionHeader">Add Footer section &nbsp; <i class="fa fa-plus-circle" aria-hidden="true"></i></h4>
        <h6 class="sectionItem">item &nbsp; <i class="fa fa-minus-circle" aria-hidden="true"></i></h6>
        <h6 class="sectionItem">item &nbsp; <i class="fa fa-minus-circle" aria-hidden="true"></i></h6>
        <h6 class="sectionItem">item &nbsp; <i class="fa fa-minus-circle" aria-hidden="true"></i></h6>
        <h6 class="sectionItem">item &nbsp; <i class="fa fa-minus-circle" aria-hidden="true"></i></h6>
      </div>
    </div>
  );
}

export default StudioSections;
