import React,{ useState } from 'react'
import Header from './header'
import Footer from './footer'
import { Link } from "react-router-dom";
import axios from 'axios';

export default class Register extends React.Component {
 onFormValueChange(e,key) {
    alert(JSON.stringify(e))
  }  
  componentDidMount() {
    const [state, setState] = useState({entityName:"",email:"",password:"",password_confirmation:""});
    axios.post(`http://localhost/example-app/public/api/register`)
      .then(res => {
        alert(res.data)
      //  const persons = res.data;
      //  this.setState({ persons });
      })
  }


  render() {
   return <div>

<Header/>

<div class="section-container">
    <div class="container">
            <div class="row">
                <div class="col-xs-12">

                    <form>
                        <fieldset>
                          <div class="form-group">
                            <label for="disabledTextInput">Entity Name</label>
                            <input type="text" onChange={this.onFormValueChange(e,"k")} id="disabledTextInput" class="form-control" placeholder="Entity Name" />
                          </div>
                          <div class="form-group">
                            <label for="disabledTextInput">Email</label>
                            <input type="text" id="disabledTextInput" class="form-control" placeholder="Email" />
                          </div>
                          <div class="form-group">
                            <label for="disabledTextInput">Password</label>
                            <input type="password" id="disabledTextInput" class="form-control" placeholder="Password" />
                          </div>
                          <div class="form-group">
                            <label for="disabledTextInput">Confirm Password</label>
                            <input type="password" id="disabledTextInput" class="form-control" placeholder="Confirm Password" />
                          </div>
                          <Link className="btn btn-primary" to="/register">Sign up</Link> &nbsp; &nbsp;
                          <Link  to="/login"> Already have account</Link>
                        </fieldset>
                      </form>

                </div>
            </div>
    </div>
</div>

<Footer/>
    </div>
  };
}
