import React, { useState, useEffect } from 'react'
import { useParams } from "react-router-dom";
import axios from 'axios';
import CustomLoader from '../custom_loader'


function Editor(props) {
  var node = props.data;

  const [laodingStatus, setlaodingStatus] = useState(false)
  const [pagesList, setPagesList] = useState([])
  const [activePageIndex, setActivePageIndex] = useState(0)

  // if (pagesList[0])
  //   nodes = JSON.parse(pagesList[0].html_sections)
  return (
    <div>
      <CustomLoader show={laodingStatus} />
      <div class="col-xs-3 studioColumn " style={{padding:"30px"}}>
        <h3>{node && (node.name)}</h3>
        <hr/>
        <div>
          <div class="row">
            <h5 class="col-md-12">Header text</h5>
          </div>
          <div class="row" >
            <textarea class="col-md-10 center" rows="1" ></textarea>
          </div>
          <br/>
          <div class="row">
            <h5 class="col-md-12">Description text</h5>
          </div>
          <div class="row" >
            <textarea class="col-md-10 center" rows="1" ></textarea>
          </div>
        </div>
      </div>
      </div>
  )
}
export default Editor;
