import React from 'react'

 function StudioSections(props) {
  var nodes = props.data
  var addNodeHeader = (nodeNmae)=>{
    props.updateNodes({type:"addHeader",data:{name:nodeNmae}})
  }

  var addImage = (nodeName)=>{
    props.updateNodes({type:"addImage",data:{name:nodeName}})
  }

  var removeNode = ($id)=>{
    props.updateNodes({type:"removeNode",data:{id:$id}})
  }
  var updateNode = ($name)=>{
    props.updateNodes({type:"updateNode",data:{name:$name}})
  }
  return (
    <div>
       <div class="col-xs-3 studioColumn">
        <a href="#" onClick={(e)=>{e.preventDefault();addNodeHeader('new header');}}><h4 class="sectionHeader" id="addHeader">Add Header section &nbsp; <i class="fa fa-plus-circle" aria-hidden="true"></i></h4></a>
        {nodes.map((item)=>{
          if(item.type == "header")
          return  (  <h6 class="sectionItem"> <input type="text"  onChange={(e)=>{item.name = e.currentTarget.value}} placeholder={item.name} /> &nbsp;
           <i class="fa fa-minus-circle fa-lg" onClick={(e)=>{removeNode(item.id)}} aria-hidden="true"></i>
          &nbsp; &nbsp; <i  onClick={(e)=>{updateNode(item.name)}} class="fa fa-pencil-square-o fa-lg" aria-hidden="true"></i>
          </h6> )
        })}
        <h4 class="sectionHeader">Add Menu items section &nbsp; <i class="fa fa-plus-circle" aria-hidden="true"></i></h4>
        <h6 class="sectionItem">item &nbsp; <i class="fa fa-minus-circle" aria-hidden="true"></i></h6>
        <a href="#" onClick={(e)=>{e.preventDefault();addImage('new image');}}><h4 class="sectionHeader" id="addHeader">Add Header section &nbsp; <i class="fa fa-plus-circle" aria-hidden="true"></i></h4></a>
        {nodes.map((item)=>{
          if(item.type == "image")
          return  (  <h6 class="sectionItem"> <input type="text"  onChange={(e)=>{item.name = e.currentTarget.value}} placeholder={item.name} /> &nbsp;
           <i class="fa fa-minus-circle fa-lg" onClick={(e)=>{removeNode(item.id)}} aria-hidden="true"></i>
          &nbsp; &nbsp; <i  onClick={(e)=>{updateNode(item.name)}} class="fa fa-pencil-square-o fa-lg" aria-hidden="true"></i>
          </h6> )
        })}
        <h4 class="sectionHeader">Add Footer section &nbsp; <i class="fa fa-plus-circle" aria-hidden="true"></i></h4>
        <h6 class="sectionItem">item &nbsp; <i class="fa fa-minus-circle" aria-hidden="true"></i></h6>
        <h6 class="sectionItem">item &nbsp; <i class="fa fa-minus-circle" aria-hidden="true"></i></h6>
        <h6 class="sectionItem">item &nbsp; <i class="fa fa-minus-circle" aria-hidden="true"></i></h6>
        <h6 class="sectionItem">item &nbsp; <i class="fa fa-minus-circle" aria-hidden="true"></i></h6>
      </div>
    </div>
  );
}

export default StudioSections;
