import React,{ useState } from 'react'
import Header from './header'
import Footer from './footer'
import { Link } from "react-router-dom";
import axios from 'axios';



export default function Register (props) {

    const [formState, setFormState] = useState({name:"",nameHasError:false,email:"",emailHasError:false,password:"",passwordHasError:false,password_confirmation:"",password_confirmationHasError:false});


      function submit(){
         if(formState.name.length == 0) formState.nameHasError = true ;
         if(formState.email.length == 0) setFormState({...formState,emailHasError:true}) ;
         if(formState.password.length == 0)  setFormState({...formState,passwordHasError:true}) ;
         if(formState.password_confirmation.length == 0) setFormState({...formState,password_confirmationHasError:true}) ;
         setFormState(formState)
         
        axios.post(`register`,formState)
        .then(res => {
          alert(JSON.stringify(res.data))
        }).catch(error => {
          alert(JSON.stringify(error.response.data));
        })
      
      }

  return(

    <div>

<Header/>

<div class="section-container">
    <div class="container">
            <div class="row">
                <div class="col-xs-12">
                    <form>
                        <fieldset>
                          <div class="form-group">
                            <label for="disabledTextInput">Entity Name</label>
                            <input type="text" onChange={e=>{setFormState({...formState,"name":e.currentTarget.value})}}  class="form-control" placeholder="Entity Name" />
                            {formState.nameHasError > 0 &&<p class="text-danger">this field required</p>  }               
                          </div>
                          <div class="form-group">
                            <label for="disabledTextInput">Email</label>
                            <input type="text" onChange={e=>{setFormState({...formState,"email":e.currentTarget.value})}} class="form-control" placeholder="Email" />
                            {formState.emailHasError > 0 &&<p class="text-danger">this field required</p>  } 
                          </div>
                          <div class="form-group">
                            <label for="disabledTextInput">Password</label>
                            <input type="password" onChange={e=>{setFormState({...formState,"password":e.currentTarget.value})}} class="form-control" placeholder="Password" />
                            {formState.passwordHasError > 0 &&<p class="text-danger">this field required</p>  } 
                          </div>
                          <div class="form-group">
                            <label for="disabledTextInput">Confirm Password</label>
                            <input type="password" onChange={e=>{setFormState({...formState,"password_confirmation":e.currentTarget.value})}} class="form-control" placeholder="Confirm Password" />
                            {formState.password_confirmationHasError > 0 &&<p class="text-danger">this field required</p>  } 
                          </div>
                          <a className="btn btn-primary" onClick={e=>{
                            e.preventDefault();
                             submit();
                          }
                          } >Sign up</a> &nbsp; &nbsp;
                          <Link  to="/login"> Already have account</Link>
                        </fieldset>
                      </form>

                </div>
            </div>
    </div>
</div>

<Footer/>
    </div>
  );
}
