  }, [])
  return (
    <div class={props.selectedMenu && props.selectedMenu.template_class}>
    {show &&  <div class="splash"> <h1 class="animated flip splashTitle">{props.selectedMenu && props.selectedMenu.name}</h1></div>}
    </div>
  );
}

