import React,{ useState } from 'react'
import Header from './header'
import Footer from './footer'
import { Link } from "react-router-dom";
import axios from 'axios';



export default function Register (props) {

    const [formState, setFormState] = useState({name:"",nameError:"",email:"",emailError:"",password:"",passwordError:"",password_confirmation:"",password_confirmationError:""});
    const errors = {name:"The name field is required"}

      function submit(){
         if(formState.name.length == 0)  formState.nameError = "" 
         else formState.nameHasError = false 
         if(formState.email.length == 0) formState.emailHasError=true
         else formState.emailHasError = false 
         if(formState.password.length == 0)  formState.passwordHasError=true
         else formState.passwordHasError = false 
         if(formState.password_confirmation.length == 0) formState.password_confirmationHasError = true 
         else formState.password_confirmationHasError = false 
         alert(JSON.stringify(formState))
         setFormState({...formState})
         
        axios.post(`register`,formState)
        .then(res => {
          alert(JSON.stringify(res.data))
        }).catch(error => {
           let errors =  error.response.data.errors;
           if(errors.hasOwnProperty("name")) formState.nameHasError = true 
           else formState.nameHasError = false 
           if(errors.hasOwnProperty("email")) formState.emailHasError=true
           else formState.emailHasError = false 
           if(errors.hasOwnProperty("password"))  formState.passwordHasError=true
           else formState.passwordHasError = false 

           if(errors.hasOwnProperty("password_confirmation")) formState.password_confirmationHasError = true 
           else formState.password_confirmationHasError = false 
           setFormState({...formState})
        })
      
      }

  return(

    <div>

<Header/>

<div class="section-container">
    <div class="container">
            <div class="row">
                <div class="col-xs-12">
                    <form>
                        <fieldset>
                          <div class="form-group">
                            <label for="disabledTextInput">Entity Name</label>
                            <input type="text" onChange={e=>{setFormState({...formState,"name":e.currentTarget.value})}}  class="form-control" placeholder="Entity Name" />
                            {formState.nameError > 0 &&<p class="text-danger">{formState.nameError}</p>  }               
                          </div>
                          <div class="form-group">
                            <label for="disabledTextInput">Email</label>
                            <input type="text" onChange={e=>{setFormState({...formState,"email":e.currentTarget.value})}} class="form-control" placeholder="Email" />
                            {formState.emailError.length > 0 &&<p class="text-danger">The email field is required.</p>  } 
                          </div>
                          <div class="form-group">
                            <label for="disabledTextInput">Password</label>
                            <input type="password" onChange={e=>{setFormState({...formState,"password":e.currentTarget.value})}} class="form-control" placeholder="Password" />
                            {formState.passwordError.length > 0  &&<p class="text-danger">The password field is required.</p>  } 
                          </div>
                          <div class="form-group">
                            <label for="disabledTextInput">Confirm Password</label>
                            <input type="password" onChange={e=>{setFormState({...formState,"password_confirmation":e.currentTarget.value})}} class="form-control" placeholder="Confirm Password" />
                            {formState.password_confirmationError.length > 0  &&<p class="text-danger">The password_confirmation field is required</p>  } 
                          </div>
                          <a className="btn btn-primary" onClick={e=>{
                            e.preventDefault();
                             submit();
                          }
                          } >Sign up</a> &nbsp; &nbsp;
                          <Link  to="/login"> Already have account</Link>
                        </fieldset>
                      </form>

                </div>
            </div>
    </div>
</div>

<Footer/>
    </div>
  );
}
