import React, { useState, useEffect } from 'react'
import { useParams } from "react-router-dom";
import axios from 'axios';
import { actions, nodeTypes } from '../reducers/studio_reducer'
import CustomLoader from '../custom_loader'


function Preview(props) {
  var nodes = []
  const [laodingStatus, setlaodingStatus] = useState(false)
  const [pagesList, setPagesList] = useState([])
  const [activePageIndex, setActivePageIndex] = useState(0)
  const { menuId } = useParams()

  function getMenuPages() {
    setlaodingStatus(true)
    axios.get('/page/show?menu_id=' + menuId)
      .then(res => {
        setlaodingStatus(false)
        setPagesList(res.data)
      }).catch(error => {
        setlaodingStatus(false)
        alert(error.message)
      })
  }
  useEffect(() => {
    getMenuPages();
  }, [])

  if (pagesList[0])
    nodes = JSON.parse(pagesList[0].html_sections)
  return (
    <div>
      <CustomLoader show={laodingStatus} />
      <div id="menuPage">
        <ul>
          {nodes.map((item) => {
            if (item.type == nodeTypes.header)
              return (<li>
                <div class="headerSection" style={{background:item.color}}>
                 <h1>{item.title}</h1>
                 <h6>{item.description}</h6>
                </div>
              </li>)
            else if (item.type == nodeTypes.image)
              return (<li>
                <div class="imageSection">
                  <img width="100%" height="100px" src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR5_4qsgI6It1r_h-Srxze4ATdDymHwQ7NaVA&usqp=CAU" />
                </div>
              </li>)
            else if (item.type == nodeTypes.items)
              return (<li>
                <ul>
                  <li>
                    <div class="itemSection">
                      <p>item</p> <span>250$</span>
                    </div>
                  </li>
                </ul>
              </li>)
            else if (item.type == nodeTypes.footer)
              return (
                <li>
                  <div class="footerSection">
                    <ul>
                      <li><i class="fa fa-circle-o" aria-hidden="true"></i> <h6>footer content</h6></li>
                    </ul>
                  </div>
                </li>)
          })}
        </ul>
      </div>
    </div>
  )
}
export default Preview;
